/**
 * Shuffles the options array for a question and returns the new correct index.
 * We use a deterministic shuffle based on the ID for consistency in this file,
 * but ensuring the correct answer is no longer always at index 0.
 */
const shuffle = (id: number, correct: string, wrongs: string[]): { options: string[], correctAnswer: number } => {
  const options = [correct, ...wrongs];
  // Deterministic "random" index based on ID
  const targetIndex = (id * 13) % 4;
  
  // Swap original correct (at 0) with the target index
  const temp = options[targetIndex];
  options[targetIndex] = options[0];
  options[0] = temp;
  
  return { options, correctAnswer: targetIndex };
};

